(3252, 9622, '', 15, 'The love we give away is the only love we keep.', '', 0, 2, '2022-12-20 21:08:45', '2023-01-27 00:30:37'),
(3253, 73930, '', 15, 'The giving of love is an education in itself.', '', 0, 2, '2022-12-20 21:08:45', '2023-01-27 00:30:37'),
(3254, 61530, '', 15, 'A man is already halfway in love with any woman who listens to him.', '', 0, 2, '2022-12-20 21:08:45', '2023-01-27 00:30:37'),
(3255, 74924, '', 15, 'Women are meant to be loved, not to be understood.', '', 0, 2, '2022-12-20 21:08:45', '2023-01-27 00:30:37'),
