(3358, 75135, '', 15, 'Love conquers all things except poverty and toothache.', '', 0, 2, '2022-12-20 21:08:45', '2023-01-27 00:30:38'),
(3359, 27362, '', 15, 'When you fish for love, bait with your heart, not your brain.', '', 0, 2, '2022-12-20 21:08:45', '2023-01-27 00:30:38'),
