(11507, 32672, '', 14, '#1 relationship rule: over communicate.', '', 0, 2, '2023-01-19 19:42:42', '2023-01-27 00:32:09'),
(11508, 17637, '', 14, 'When you love someone, you want to take care of that person as you would take care of yourself.', '', 0, 2, '2023-01-19 19:42:42', '2023-01-27 00:32:09'),
(11509, 55661, '', 14, 'Find someone who makes reality better than dreams and do your best to deserve them.', '', 0, 2, '2023-01-19 19:42:42', '2023-01-27 00:32:09'),
(11510, 72024, '', 14, 'To the world, you may be one person, but to one person you are the world.', '', 0, 2, '2023-01-19 19:42:42', '2023-01-27 00:32:09'),
